import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    products: [],
  },
  reducers: {
    addProduct: (state, action) => {

      state.products = action.payload;

    },
  },
});

export const { addProduct } = cartSlice.actions;
export default cartSlice.reducer;
