import React from 'react'
import styled from 'styled-components'
import axios from "axios";
import {useHistory} from "react-router";


const Container=styled.div`
width:100vw;
height:97vh;
background:linear-gradient(rgba(255,255,255,0.2),rgba(255,255,255,0.2)),url("https://cdn.pixabay.com/photo/2021/02/08/12/40/lasagna-5994612_960_720.jpg")center/cover no-repeat;
display:flex;
align-items:center;
justify-content:center;`

const Wrapper=styled.div`
padding:20px;
width:40%;
background-color:white;
border-radius:10px;
border:2px solid teal;`

const Title=styled.h1`font-size:24px;
font-weight:300;`
const Form=styled.form`
display:flex;
flex-wrap:wrap;`

const Input=styled.input`
flex:1;
min-width:40%;
margin:20px 10px 0 0;
padding:10px;`
const Agreement=styled.span`
font-size:12px;
margin:20px 0;`
const Button=styled.button`
width:40%;
border:none;
padding:15px 20px;
background-color:teal;
color:white;
cursor:pointer;`

export const Register = () => {
    const handleClick= async (e)=>{  
        e.preventDefault();
        if(passwordAgain.current.value!== password.current.value){
            password.current.setCustomValidity("Password dont match!!");
        }else{
            const user={
                username:username.current.value,
                email:email.current.value,
                password:password.current.value,
            }
            try{
            await axios.post("/auth/register",user);
            history.push("/login");

            }catch(err){
                console.log(err);
            }
        }
    };
    return (
        <Container>
            <Wrapper>
                <Title>Create a acoount</Title>
                <Form onSubmit={handleClick}>
                    <Input required ref={firstname} placeholder="first name"/>
                    <Input required ref={lastname} placeholder="last name"/>
                    <Input required ref={email} placeholder="email"/>
                    <Input required ref={username} placeholder="username"/>
                    <Input required ref={username} placeholder="username"/>
                    <Input required ref={password} placeholder="password"/>
                    <Input required ref={confirmpassword} placeholder="confirm password"/>
                    <Agreement>By creating a account,I consent to the processing of my Personal data in accordance
                        with the<b>PRIVACY POLICY</b>
                    </Agreement>
                    <Button type="submit"> Create</Button>
                </Form>
            </Wrapper>
        </Container>
    )
}
