import React, { useEffect } from 'react'
import styled from 'styled-components'
import { Footer } from '../components/Footer'
import { Newsletter } from '../components/Newsletter'
// import { AllProduct } from '../components/AllProduct'
import { useLocation } from 'react-router'
import { useState } from 'react'
import "./App.css"
import { publicRequest } from '../requestMethod'
import Container from './viewProduct/Container'


const Title = styled.h1`
margin:20px;
color:white;`



export const ProductList = () => {
    const location = useLocation();
    const cat = (location.pathname.split("/")[2]);
    const [foodItem, setFoodItem] = useState([])

    useEffect(() => {
        const getAllItems = async () => {
            const res = await publicRequest.get("/resturents/allProducts");

            setFoodItem(res.data.filter((f) => f.type === cat ? cat : ""));
        }; getAllItems();
    }, [])

    // console.log(foodItem)

    return (
        <>
            <div className="app__bg container">

                <Title>{`Top ${cat} Items`}</Title>
                {/* <AllProduct cat={cat} filters={filters} sort={sort}/> */}
                {foodItem?.map((item) => (
                    <Container item={item} />
                ))}

            </div>
            <Newsletter />
            <Footer />
        </>
    )
}
