import React from 'react'
import styled from 'styled-components'

const Container=styled.div`
width:100vw;
height:97vh;
background:linear-gradient(rgba(255,255,255,0.2),rgba(255,255,255,0.2)),url("https://media.istockphoto.com/photos/empty-dark-wooden-table-in-front-of-abstract-blurred-bokeh-background-picture-id1130714046")center/cover no-repeat;
display:flex;
align-items:center;
justify-content:center;`

const Wrapper=styled.div`
padding:20px;
width:40%;
background-color:white;
border-radius:10px;
border:2px solid teal;`

const Title=styled.h1`font-size:24px;
font-weight:300;`
const Form=styled.form`
display:flex;
flex-wrap:wrap;`

const Input=styled.input`
flex:1;
min-width:40%;
margin:20px 10px 0 0;
padding:10px;`

const Button=styled.button`
width:40%;
border:none;
padding:15px 20px;
background-color:teal;
color:white;
cursor:pointer;`

const Link=styled.span`
font-size:24px;
font-weight:300;
margin:20px 0;`

export const Login = () => {
    return (
        <Container>
        <Wrapper>
            <Title>Sign-In</Title>
            <Form>
                <Input placeholder="username"/>
                <Input placeholder="password"/>
                <Button> Login</Button>
                <Link>Forget Password..</Link>
                <Link>Create a New</Link>
            </Form>
        </Wrapper>
    </Container>
    )
}
