import React from 'react'
import styled from 'styled-components'
const Container=styled.div`
width:100vw;
height:97vh;
background:linear-gradient(rgba(255,255,255,0.2),rgba(255,255,255,0.2)),url("https://cdn.pixabay.com/photo/2021/02/08/12/40/lasagna-5994612_960_720.jpg")center/cover no-repeat;
display:flex;
align-items:center;
justify-content:center;`

const Wrapper=styled.div`
padding:20px;
width:40%;
background-color:white;
border-radius:10px;
border:2px solid teal;`

const Title=styled.h1`font-size:24px;
font-weight:300;`
const Form=styled.form`
display:flex;
flex-wrap:wrap;`

const Input=styled.input`
flex:1;
min-width:40%;
margin:20px 10px 0 0;
padding:10px;`
const Agreement=styled.span`
font-size:12px;
margin:20px 0;`
const Button=styled.button`
width:40%;
border:none;
padding:15px 20px;
background-color:teal;
color:white;
cursor:pointer;`

export const Register = () => {
    return (
        <Container>
            <Wrapper>
                <Title>Create a acoount</Title>
                <Form>
                    <Input placeholder="first name"/>
                    <Input placeholder="last name"/>
                    <Input placeholder="email"/>
                    <Input placeholder="username"/>
                    <Input placeholder="password"/>
                    <Input placeholder="confirm password"/>
                    <Agreement>By creating a account,I consent to the processing of my Personal data in accordance
                        with the<b>PRIVACY POLICY</b>
                    </Agreement>
                    <Button> Create</Button>
                </Form>
            </Wrapper>
        </Container>
    )
}
