import "./products.css"
import React from "react";
import Product from "../product/Product";
import DiningIcon from '@mui/icons-material/Dining';
import { useEffect, useState } from "react";
import { userRequest } from "../../requestMethod";
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';



export const Products = () => {
    const [filtered, setFiltered] = useState([])
    const [change, setChange] = useState('');
    const [resturent, setResturent] = useState([])
    const [isSet, setIsSet] = useState(false);

    let filterData = [];

    useEffect(() => {
        const getResturents = async () => {
            try {
                const res = await userRequest.get("/resturents");
                setResturent(res.data);
                setFiltered(res.data)
                for (let i = 0; i < res.data.length; i++) {
                    const data = {
                        name: res.data[i].name,
                        id: res.data[i].id,
                        img: res.data[i].img,
                        desc: res.data[i].desc,
                        rating: res.data[i].rating,
                        type: res.data[i].type,
                    }
                    filterData.push(data)
                }
            } catch (err) {
                console.log(err);
            }
        }; getResturents();
    }, [])

    console.log(filterData)


    const [state, setState] = React.useState({
        top: false,
        left: false,
        bottom: false,
        right: false,
    });


    const toggleDrawer = (anchor, open) => (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }
        setState({ ...state, [anchor]: open });
    };

    useEffect(() => {
        setIsSet(true);
        const getAnsType = async () => {
            if (change === 'rating') {
                const data = await filtered.sort((a, b) => { return b.rating - a.rating });
                setFiltered(data)

            } else if (change === 'delivery') {
                const data = await filtered.sort((p1, p2) => { return new Date(p2.createdAt) - new Date(p1.createdAt) });
                setFiltered(data)

            } else if (change === 'revelance') {
                const data = await filtered.sort((p1, p2) => { return p1.rating - p2.rating });
                setFiltered(data)
            }
        }; getAnsType();

    }, [change])

    const list = (anchor) => (
        <Box
            sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 250 }}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
        >
            <List>
                {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>
                            {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                        </ListItemIcon>
                        <ListItemText primary={text} />
                    </ListItem>
                ))}
            </List>
            <Divider />
            <List>
                {['All mail', 'Trash', 'Spam'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>
                            {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                        </ListItemIcon>
                        <ListItemText primary={text} />
                    </ListItem>
                ))}
            </List>
        </Box>
    );



    return <div className='app__darkbg app__wrapper section__padding'>
        <div className='app__products-wrapper'>
            <div className='app__products-wrapper-top'>
                <div className='app__products-wrapper_text'>
                    <h1 className='headtext_cormorent'>Our Services</h1>
                </div>
                <div className='app__products-wrapper_options'>
                    <a onClick={() => setChange('revelance')}>Revelance</a>
                    <a onClick={() => setChange('delivery')}>Delivery</a>
                    <a onClick={() => setChange('rating')}>Rating</a>
                    <div className="sortSection" onClick={toggleDrawer('right', true)}>
                        <button className="sort" >Filter</button>
                        <DiningIcon />
                    </div>

                </div>

                <Drawer
                    anchor={'right'}
                    open={state['right']}
                    onClose={toggleDrawer('right', false)}
                >
                    {list('right')}
                </Drawer>
            </div>

            <div className='app__products-wrapper-bottom'>
                {filtered.map((res) => (
                    <Product item={res} />
                ))}

            </div>

        </div>
    </div>;
};
export default Products;
