import React, { useState, useEffect } from 'react'

import "./cartItem.css"
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { addProduct } from '../../redux/cartRedux'

const CartItem = ({ item, addtocart }) => {
    const dispatch = useDispatch();
    const foodItem = useSelector(state => state.food.foods);
    const foodPrice = useSelector(state => state.food.total);
    console.log(foodItem)


    // useEffect(() => {
    //     // dispatch(addProduct({ ...food, quantity }))  
    //     addtocart && (
    //         foodItem.map((food)=>(
    //          dispatch(addProduct({ ...food, quantity }))
    //         ))
    //     )
    // }, [addtocart])


    return (

        <div className='CartItem'>

            <div className="cartItemComponent">
                <div className="cartItemDesc">
                    <span className="cartItemProduct">{item.cat}</span>
                    <img src={item.img} alt="" />
                </div>
                <div className="cartItemDetails">
                    <span className="price">{foodPrice}</span>

                </div>
            </div>
        </div>

    )
}

export default CartItem
