import "./products.css"
import React from "react";
import Product from "../product/Product";
import DiningIcon from '@mui/icons-material/Dining';
import { useEffect, useState } from "react";
import { userRequest } from "../../requestMethod";
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';




export const Products = () => {
    const [resturents, setResturents] = useState([]);
    const [filter, setFilter] = useState([]);
    const [change, setChange] = useState(false);
    useEffect(() => {
        const getResturents = async () => {
            const resturent = await userRequest.get("/resturents");
            setResturents(resturent.data);
            setFilter(resturent.data);
        }; getResturents();
    }, [change])

    // console.log(resturents)
    const [state, setState] = React.useState({
        top: false,
        left: false,
        bottom: false,
        right: false,
    });

    const toggleDrawer = (anchor, open) => (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }

        setState({ ...state, [anchor]: open });

    };
    const handleChangeFilter = () => {
        setFilter(resturents.sort((p1, p2) => { return p2.rating - p1.rating }));
    }

    const list = (anchor) => (
        <Box
            sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 250 }}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
        >
            <List>
                {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>
                            {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                        </ListItemIcon>
                        <ListItemText primary={text} />
                    </ListItem>
                ))}
            </List>
            <Divider />
            <List>
                {['All mail', 'Trash', 'Spam'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>
                            {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                        </ListItemIcon>
                        <ListItemText primary={text} />
                    </ListItem>
                ))}
            </List>
        </Box>
    );

    return <div className='app__darkbg app__wrapper section__padding'>
        <div className='app__products-wrapper'>
            <div className='app__products-wrapper-top'>
                <div className='app__products-wrapper_text'>
                    <h1 className='headtext_cormorent'>Our Services</h1>
                </div>
                <div className='app__products-wrapper_options'>
                    <a href="#revelence" value={"first"} >Revelance</a>
                    <a href="#revelence" value={"second"} >Delivery</a>
                    <a href="#revelence" value={"third"} onClick={() => handleChangeFilter}>Rating</a>
                    <div className="sortSection" onClick={toggleDrawer('right', true)}>
                        <button className="sort" >Filter</button>
                        <DiningIcon />
                    </div>

                </div>

                <Drawer
                    anchor={'right'}
                    open={state['right']}
                    onClose={toggleDrawer('right', false)}
                >
                    {list('right')}
                </Drawer>
            </div>
            <div className='app__products-wrapper-bottom'>
                {filter.map((item) => (
                    <Product item={item} />
                ))}

            </div>

        </div>
    </div>;
};
export default Products;
