import { createSlice } from "@reduxjs/toolkit";

const foodSlice = createSlice({
    name: "food",
    initialState: {
        foods: [
            item = {
                cat: "",
                price: "",
                details: "",
                type: "",
                img: "",
                fquantity: 0,
            }
        ],
        quantity: 0,

    },
    reducers: {
        addFood: (state, action) => {
            item = action.payload;
            foods.push(item);
            return state;
        },
    },
});

export const { addFood } = foodSlice.actions;
export default foodSlice.reducer;
