import React, { useState } from 'react'
import { addProduct } from "../../redux/cartRedux";
import BookmarkAddedIcon from '@mui/icons-material/BookmarkAdded';
import { useDispatch } from "react-redux";
import { useSelector } from 'react-redux';


const Container = ({ item }) => {
    const [slice, setSlice] = useState({})
    const resturent = useSelector(state => state.resturent)
    const dispatch = useDispatch();
    console.log(resturent)

    const handleAdd = (ide) => {
        setSlice(resturent.products.find((p) => p._id === ide))
        console.log(slice)
        dispatch(addProduct({ ...slice }))

    }
    return (
        <div>
            <div className="centerContainer">
                <div className="containerLeft">

                    <div style={{ display: "flex", alignItems: "flex-start", marginBottom: "10px" }}><BookmarkAddedIcon className="bookmarkicon" />
                        <span className="foodTitle">{item.cat}</span>
                    </div>
                    <div className="foodPrice">{item.price + " Rs"}</div>
                    <p style={{ fontSize: "16px", color: "#555" }}>{item.details}</p>

                </div>
                <div className="containerRight">
                    <img src={item.img} alt="" className="foodImg" />
                    <button className="saveFood" onClick={() => handleAdd(item._id)}>Add Item</button>
                </div>
            </div>
        </div>
    )
}

export default Container
