import "./products.css"
import React from "react";
import Product from "../product/Product";
import DiningIcon from '@mui/icons-material/Dining';
import { useEffect, useState } from "react";
import { userRequest } from "../../requestMethod";
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: '#fff',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};


export const Products = () => {
    const [resturents, setResturents] = useState([]);
    useEffect(() => {
        const getResturents = async () => {
            const resturent = await userRequest.get("/resturents");
            setResturents(resturent.data);
        }; getResturents();
    }, [])

    // console.log(resturents)
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    return <div className='app__darkbg app__wrapper section__padding'>
        <div className='app__products-wrapper'>
            <div className='app__products-wrapper-top'>
                <div className='app__products-wrapper_text'>
                    <h1 className='headtext_cormorent'>Our Services</h1>
                </div>
                <div className='app__products-wrapper_options'>
                    <a href="#revelence" onClick={handleOpen}>Revelance</a>
                    <a href="#revelence">Delivery</a>
                    <a href="#revelence">Rating</a>
                    <div className="sortSection">
                        <button className="sort">Filter</button>
                        <DiningIcon />
                    </div>

                </div>
                <div>
                    <Modal
                        open={open}
                        onClose={handleClose}
                        aria-labelledby="modal-modal-title"
                        aria-describedby="modal-modal-description"
                    >
                        <Box sx={style}>
                            <h1>Hi i am a modal</h1>
                        </Box>
                    </Modal>
                </div>
            </div>
            <div className='app__products-wrapper-bottom'>
                {resturents.map((item) => (
                    <Product item={item} />
                ))}

            </div>

        </div>
    </div>;
};
export default Products;
