import React from 'react'
import styled from 'styled-components'
import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';

const Container = styled.div`
width:100%;
height:100vh;
display:flex;
position:relative;`

const Arrow = styled.div`
width:50px;
height:50px;

border-radius:50%;
display:flex;
align-items-center;
justify-content:center;
position:absolute;
top:0;
bottom:0;
left:${props => props.direction === "left" && "10px"};
right:${props => props.direction === "right" && "10px"};
margin:auto;
cursor:pointer;
opacity:0.7;`

const Wrapper = styled.div`
height:100%;`

const Slide = styled.div`
display:flex;
align-items:center;
width:100vw;
height:100vh;`
const ImgContainer = styled.div`
flex:1;
height:100%;
margin-left:20px;`

const Image = styled.img`
height:80%;`
const InfoContainer = styled.div`
flex:1;`


export const Slider = () => {
    return (
        <Container>
            <Arrow direction="left">
                <ArrowCircleLeftIcon />
            </Arrow>
            <Wrapper>
                <Slide>

                <ImgContainer>
                <Image src="https://images.unsplash.com/photo-1537047902294-62a40c20a6ae?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1935&q=80"/>
                </ImgContainer>
                <InfoContainer></InfoContainer>
                </Slide>
            </Wrapper>

            <Arrow direction="right">
                <ArrowCircleRightIcon />
            </Arrow>
        </Container>
    )
}
