import React, { useEffect } from 'react'
import styled from 'styled-components'
import { Announcement } from '../components/Announcement'
import { Footer } from '../components/Footer'
import { Newsletter } from '../components/Newsletter'
// import { AllProduct } from '../components/AllProduct'
import { useLocation } from 'react-router'
import { useState } from 'react'

import { publicRequest } from '../requestMethod'
import Container from './viewProduct/Container'


const Title = styled.h1`
margin:20px;`
const FilterContainer = styled.div`
display:flex;
justify-content:space-between;`


export const ProductList = () => {
    const location = useLocation();
    const cat = (location.pathname.split("/")[2]);
    const [foodItem, setFoodItem] = useState([])

    useEffect(() => {
        const getAllItems = async () => {
            const res = await publicRequest.get("/resturents/allProducts");

            setFoodItem(res.data.filter((f) => f.type === cat ? cat : ""));
        }; getAllItems();
    }, [])

    // console.log(foodItem)

    return (
        <Container className="app__bg">

            <Title>Yummy</Title>
            {/* <AllProduct cat={cat} filters={filters} sort={sort}/> */}
            {foodItem?.map((item) => (
                <Container item={item} />
            ))}
            <Newsletter />
            <Footer />
        </Container>
    )
}
