import React from 'react'
import styled from 'styled-components'
import { Announcement } from '../components/Announcement'
import { Footer } from '../components/Footer'
import Navbar from '../components/Navbar'
import { Newsletter } from '../components/Newsletter'
import Add from '@mui/icons-material/Add';
import Remove from '@mui/icons-material/Remove';


const Container=styled.div``
const Wrapper=styled.div`
padding:50px;
display:flex;`
const ImgContainer=styled.div`
flex:1;
`



const Image=styled.img`
width:90%;
height:80vh;
object-fit:cover;
max-width:89%;
`


const InfoContainer=styled.div`
flex:1;
padding:0px 50px;
`
const Title=styled.h1`
font-weight:200;
`

const Desc=styled.p`
margin:20px 0;`


const Price=styled.span`
font-weight:100;
font-size:40px;`

const  FilterContainer=styled.div`
width:50%;
margin:30px 0;
display:flex;
justify-content:space-between`


const  Filter=styled.div`
display:flex;'
align-items:center;`


const  FilterTitle=styled.span`
font-size:20px;
font-weight:200;

`
const  FilterSize=styled.select`
margin-left:20px;
padding:5px;`
const  FilterSizeOption=styled.option`
list-style:none;`

const AddContainer=styled.div`
display:flex;
align-items:center;
width:50%;
justify-content:space-between;`


const AmountContainer=styled.div`
display:flex;
align-items:center;
font-weight:700;`


const Amount=styled.span`
width:30px;
height:30px;
border-radius:10px;
border:1px solid teal;
align-items:center;
justify-content:center;`


const Button=styled.button``


export const Product = () => {
    return (
        <Container>
            <Navbar/>
            <Announcement/>
            <Wrapper>
            <ImgContainer>
            <Image src="https://cdn.pixabay.com/photo/2018/04/17/23/04/food-3329079_960_720.png"/>
            </ImgContainer>
            <InfoContainer>
                <Title>Chicken Laziz</Title>
                <Desc>..Want to eat tasty please came with your besty..</Desc>
                <Price>240 rs.</Price>
                <FilterContainer>
                    <Filter><FilterTitle>Size</FilterTitle>
                    <FilterSize>
                    <FilterSizeOption>Samll</FilterSizeOption>
                    <FilterSizeOption>Medium</FilterSizeOption>
                    <FilterSizeOption>Large</FilterSizeOption>
                    </FilterSize>
                    </Filter>
                </FilterContainer>
                <AddContainer>
                    <AmountContainer>
                        <Remove/>
                        <Amount>1</Amount>
                        <Add/>
                    </AmountContainer>
                    <Button>ADD TO CART</Button>
                </AddContainer>
            </InfoContainer>
            </Wrapper>
            <Newsletter/>
            <Footer/>
        </Container>
    )
}
export default Product;
