import "./product.css"
import StarBorderIcon from '@mui/icons-material/StarBorder';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { useState } from "react";
import { Link } from "react-router-dom";
import { orange } from "@mui/material/colors";
import FavoriteIcon from '@mui/icons-material/Favorite';
import { useSelector } from "react-redux";

export const Product = ({ item }) => {

    function toRad(Value) {
        return Value * Math.PI / 180;
    }

    function calcCrow(lat1, lon1, lat2, lon2) {
        var R = 6371; // km
        var dLat = toRad(lat2 - lat1);
        var dLon = toRad(lon2 - lon1);
        var lat1 = toRad(lat1);
        var lat2 = toRad(lat2);

        var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.sin(dLon / 2) * Math.sin(dLon / 2) * Math.cos(lat1) * Math.cos(lat2);
        var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        var d = (R * c);
        var p = Math.floor(d / 35);
        var point = ((Math.floor(d % 35)) * 60).toFixed(2);
        return `${p} Hour ${point} Minutes`;

    }


    return (
        <div className='product'>

            <div className="productTop">
                <img src={item.img} alt="" />
            </div>

            <div className="productBottom">
                <Link to={`/resturent/${item._id}`}>
                    <h3 className="title">{item.name}</h3>
                </Link>
                <p className="desc">{item.desc}</p>
                <div className="productDesc">
                    <div className="star">
                        <StarBorderIcon />
                        <span className="starvalue">{item.rating}</span>
                    </div>
                    <div className="dash"></div>
                    <FavoriteIcon />
                    {calcCrow(22.5726, 88.3639, 22.5958, 88.2636)}
                    <div className="dash"></div>

                    <select name="type" className="select">
                        {item.type.map((item) => (
                            <option className="option" value={`${item}`}>{item}</option>
                        ))}
                    </select>

                </div>
                <p className="productTime">50% off and Welcomised</p>
            </div>
        </div>
    )
}
export default Product;
