import "./register.css"
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { publicRequest } from "../../requestMethod";
import FormInput from "./FormInput";

export const Register = () => {
    const history = useHistory();

    const [values, setValues] = useState({
        username: "",
        email: "",
        birthday: "",
        password: "",
        confirmPassword: "",
    });



    const handleClick = async (e) => {
        e.preventDefault();

        try {
            console.log(values);
            await publicRequest.post("/auth/register", values);
            history.push("/login");

        } catch (err) {
            console.log(err);
        }
    };
    const onChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };
    return (
        <div className="container">
            <div className="wrapper">
                <span className="title">Create a acoount</span>
                <form className="form" onSubmit={handleClick}>
                    <div className="inputs">
                        {inputs.map((input) => (
                            <FormInput
                                key={input.id}
                                {...input}
                                value={values[input.name]}
                                onChange={onChange}
                            />
                        ))}
                    </div>

                    <span className="agreement">By creating a account,I consent to the processing of my Personal data in accordance
                        with the<b>PRIVACY POLICY</b>
                    </span>
                    <button type="submit" className="button"> Create</button>
                </form>
            </div>
        </div>
    )
}
