import { Card, CardActionArea, CardContent, CardMedia, Typography } from '@mui/material'
import React, { useState } from 'react'
import { Button, CardActions } from '@mui/material'
import Add from '@mui/icons-material/Add'
import AddShoppingCartOutlined from '@mui/icons-material/AddShoppingCartOutlined'
import Remove from '@mui/icons-material/Remove'

const ContainerItem = ({ item, index }) => {
    const [quantity, setQuantity] = useState(1);


    const handleQuantity = (type) => {
        if (type === "desc") {
            quantity > 1 && setQuantity(quantity - 1);
        } else {
            setQuantity(quantity + 1);
        }
    };
    return (
        <div style={{ marginTop: `${index * "100px"}`, margin: "0px 20px" }}>
            <Card sx={{ maxWidth: 345, maxHeight: 500, backgroundColor: "transparent" }}>
                <CardActionArea sx={{ color: "#D3D3D3" }}>
                    <CardMedia
                        component="img"
                        height="180"
                        image={item?.img}
                        alt="green iguana"
                    />
                    <CardContent sx={{ color: "white" }}>
                        <Typography gutterBottom variant="h5" component="div">
                            {item?.cat}
                        </Typography>
                        <Typography variant="body2" color="white">
                            {item?.details}
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions sx={{ display: "flex", justifyContent: "space-between" }}>
                    <Button sx={{ backgroundColor: "yellow" }} variant="contained" endIcon={<AddShoppingCartOutlined style={{ color: "white" }} />}>
                        Add Item
                    </Button>
                    <div className="quantityclass">
                        <Add onClick={() => handleQuantity("add")} />
                        {quantity}
                        <Remove onClick={() => handleQuantity("desc")} />
                    </div>
                </CardActions>
            </Card>
        </div>
    )
}

export default ContainerItem
