import "./viewProduct.css"

import products from "../../constants/data"
import BookmarkAddedIcon from '@mui/icons-material/BookmarkAdded';
import { useEffect, useState } from "react";
import { userRequest, publicRequest } from "../../requestMethod";
import { useLocation } from "react-router-dom";

export const ViewProduct = () => {
    const location = useLocation();
    const id = location.pathname.split("/")[2];

    const [resturent, setResturent] = useState([]);

    useEffect(() => {
        const getResturents = async () => {
            const res = await publicRequest.get(`/resturents/find/${id}`);
            setResturent(res.data);
        }; getResturents();
    }, [])

    const product = resturent.products;

    function getProduct(type) {
        return product.filter(item => item.type === type)
    }

    console.log(resturent)


    return (
        <div className='app__bg section__padding viewProduct'>
            <h1 className="menuList">Menu items</h1>
            <div className="viewProduct_main">
                <div className="viewProduct-left">
                    <h3 style={{ color: "gold" }}>Recomended Items</h3>
                    <h3 style={{ color: "white" }}>Momos</h3>
                    <h3 style={{ color: "white" }}>Biriyani</h3>
                    <h3 style={{ color: "white" }}>Roll</h3>
                    <h3 style={{ color: "white" }}>Moghlai</h3>
                    <h3 style={{ color: "white" }}>Chiken Items</h3>
                    <h3 style={{ color: "white" }}>Mutton Items</h3>

                </div>
                <div className="viewProduct-center">
                    {resturent.type.map((t) => (
                        <div className="viewProduct-center-component">
                            {getProduct(t).map((item) => (
                                <>
                                    <div className="centerContainerTop">
                                        <h1 className="centerTitle">{`${type}`}</h1>
                                        <span className="valueTitle">{getProduct(type).length}</span>
                                    </div>
                                    <hr />
                                    <div className="centerContainer">

                                        <div className="containerLeft">

                                            <div style={{ display: "flex", alignItems: "flex-start", marginBottom: "10px" }}><BookmarkAddedIcon className="bookmarkicon" />
                                                <span className="foodTitle">{item.title}</span>
                                            </div>
                                            <div className="foodPrice">{item.price + " Rs"}</div>
                                            <p style={{ fontSize: "14px", color: "#555" }}>{item.subtitle}</p>
                                        </div>
                                        <div className="containerRight">
                                            <img src={item.imgUrl} alt="" className="foodImg" />
                                            <button className="saveFood">Add Item</button>
                                        </div>

                                    </div>

                                </>
                            ))}
                        </div>
                    ))}
                </div>
                <div className="viewProduct-right">
                    <h2 className="viewProduct-right-cart">
                        Add To Cart
                    </h2>
                </div>

                <div className="viewProduct-right"></div>
            </div>
        </div>
    )
}
