import { createSlice } from "@reduxjs/toolkit";

const foodSlice = createSlice({
    name: "food",
    initialState: {
        products: [],

    },
    reducers: {
        addFood: (state, action) => {
            state.food.push(action.payload);

        },
    },
});

export const { addFood } = foodSlice.actions;
export default foodSlice.reducer;
