import React from 'react'
import Add from '@mui/icons-material/Add'
import Remove from '@mui/icons-material/Remove'
import "./cartItem.css"

const CartItem = ({ quantity, setQuantity }) => {

    function handleQuantity(type) {
        if (type === "desc") {
            if (quantity > 1) {
                setQuantity(quantity - 1)
            }
        } else {
            setQuantity(quantity + 1)
        }
    }

    return (
        <div className='CartItem'>

            <div className="cartItemComponent">
                <div className="cartItemDesc">
                    <span className="cartItemProduct">Chicken Biriyani</span>
                    <img src="https://img.theculturetrip.com/1440x807/smart/wp-content/uploads/2018/03/biryani-india.jpg" alt="" />
                </div>
                <div className="cartItemDetails">
                    <span className="price">180 Rs</span>
                    <div className="quantity">
                        <Add onClick={handleQuantity("add")} />
                        {quantity}
                        <Remove onClick={handleQuantity("desc")} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CartItem
