import "./products.css"
import React from "react";
import Product from "../product/Product";
import DiningIcon from '@mui/icons-material/Dining';
import { useEffect, useState } from "react";
import { userRequest } from "../../requestMethod";
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import _ from "lodash";


export const Products = () => {
    const [change, setChange] = useState('revelance');
    const [resturent, setResturent] = useState([])
    const [filterData, setFilterData] = useState([]);
    const [sorted, setSorted] = useState([]);


    useEffect(() => {
        const getResturents = async () => {
            try {
                const res = await userRequest.get("/resturents");
                setResturent(res.data);
            } catch (err) {
                console.log(err);
            }
        }; getResturents();
    }, [])

    const filter = [];

    useEffect(() => {
        for (let i = 0; i < resturent.length; i++) {
            const data = {
                name: resturent[i].name,
                id: resturent[i]._id,
                img: resturent[i].img,
                desc: resturent[i].desc,
                rating: resturent[i].rating,
                type: resturent[i].type,
            }
            filter.push(data);
        }
        setFilterData(filter);
        setSorted(filter);

    }, [resturent])


    const [state, setState] = React.useState({
        top: false,
        left: false,
        bottom: false,
        right: false,
    });


    const toggleDrawer = (anchor, open) => (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }
        setState({ ...state, [anchor]: open });
    };

    useEffect(() => {
        console.log(change)
        if (change === 'revelance') {
            setSorted(filterData.sort((a, b) => b.name - a.name))
        } else if (change === 'rating') {
            setSorted(filterData.sort((a, b) => b.rating - a.rating))
        } else if (change === 'delivery') {
            setSorted(filterData.sort((a, b) => new Date(a.createdBy) - new Date(b.createdBy)))
        }
    }, [change])

    console.log(sorted)


    const list = (anchor) => (
        <Box
            sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 250 }}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
        >
            <List>
                {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>
                            {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                        </ListItemIcon>
                        <ListItemText primary={text} />
                    </ListItem>
                ))}
            </List>
            <Divider />
            <List>
                {['All mail', 'Trash', 'Spam'].map((text, index) => (
                    <ListItem button key={text}>
                        <ListItemIcon>
                            {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                        </ListItemIcon>
                        <ListItemText primary={text} />
                    </ListItem>
                ))}
            </List>
        </Box>
    );



    return <div className='app__darkbg app__wrapper section__padding'>
        <div className='app__products-wrapper'>
            <div className='app__products-wrapper-top'>
                <div className='app__products-wrapper_text'>
                    <h1 className='headtext_cormorent'>Our Services</h1>
                </div>
                <div className='app__products-wrapper_options'>
                    <a onClick={() => setChange('revelance')}>Revelance</a>
                    <a onClick={() => setChange('delivery')}>Delivery</a>
                    <a onClick={() => setChange('rating')}>Rating</a>
                    <div className="sortSection" onClick={toggleDrawer('right', true)}>
                        <button className="sort" >Filter</button>
                        <DiningIcon />
                    </div>

                </div>

                <Drawer
                    anchor={'right'}
                    open={state['right']}
                    onClose={toggleDrawer('right', false)}
                >
                    {list('right')}
                </Drawer>
            </div>

            <div className='app__products-wrapper-bottom'>
                {sorted &&
                    sorted.map((res) => (
                        <Product item={res} />
                    ))}

            </div>

        </div>
    </div>;
};
export default Products;
