import React, { useState, useEffect } from 'react'

import "./cartItem.css"
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { addProduct } from '../../redux/cartRedux'

const CartItem = ({ item, addtocart }) => {
    const dispatch = useDispatch();
    const foodItem = useSelector(state => state.food.foods);

    const total = foodItem.reduce((acc, curr) => {
        return acc + curr.total
    }, 0);



    useEffect(() => {
        addtocart && (
            dispatch(addProduct({ ...foodItem, total }))
        )
    }, [addtocart])


    return (

        <div className='CartItem'>

            <div className="cartItemComponent">
                <div className="cartItemDesc">
                    <span className="cartItemProduct">{item.cat}</span>
                    <img src={item.img} alt="" />
                </div>
                <div className="cartItemDetails">
                    <span className="price">â‚¹ {item.total}</span>

                </div>
            </div>
        </div>

    )
}

export default CartItem
