import React from 'react'
import styled from 'styled-components';
import AccountBoxIcon from '@mui/icons-material/AccountBox';
import VpnKeyIcon from '@mui/icons-material/VpnKey';
import { useRef } from "react";
import { loginCall } from '../apiCalls';
import { AuthContext } from '../context/AuthContext';
import { useContext } from 'react';

const Container=styled.div`
width:100vw;
height:97vh;
background:url("https://images.pexels.com/photos/1126728/pexels-photo-1126728.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940")center/cover no-repeat;
display:flex;
align-items:center;
justify-content:center;
background-size:cover;`


const Wrapper=styled.div`
padding:20px;
width:30%;
background-color:white;
border-radius:10px;
border:2px solid teal;`

const Title=styled.h1`font-size:24px;
font-weight:300;`
const Form=styled.form`
display:flex;
flex-direction:column;`

const Input=styled.input`
flex:1;
min-width:40%;
margin:10px 5px;
padding:10px;`

const Button=styled.button`
width:40%;
border:none;
padding:15px 20px;
background-color:teal;
color:white;
cursor:pointer;
margin-bottom:10px;
border-radus:5px;
margin-left:43px;
`

const Link=styled.a`
margin:10px 0;
font-size:12px;
text-decoration:underline;
cursor:pointer;`

const LoginInfo=styled.div`
display:flex;
align-items:center;
justify-content:center;`
const LoginPass=styled.div`
display:flex;
align-items:center;
justify-content:center;`
export const Login = () => {
    const username = useRef();
    const password= useRef();
    const {user,isFetching,error,dispatch}=useContext(AuthContext)


    const handleClick=(e)=>{
        e.preventDefault();
       loginCall({username:username.current.value,password:password.current.value},dispatch)
    };
    console.log(user);
    return (
        <Container>
        <Wrapper>
            <Title>Sign-In</Title>
            <Form onSubmit={handleClick}><LoginInfo>
                <AccountBoxIcon style={{fontSize:"40px"}}/>
                <Input required minLength="6" ref={username} placeholder="username"/>
                </LoginInfo>
                <LoginPass>
                    <VpnKeyIcon style={{fontSize:"40px"}}/>
                <Input required minLength="6" ref={password} placeholder="password"/>
                </LoginPass>

                <Button type="submit"> Login</Button>
                <Link>Forget Password..</Link>
                <Link>Create a New</Link>
            </Form>
        </Wrapper>
    </Container>
    )
}
